def show_places(lang):
    clear()
    inject_futuristic_css()
    
    header = f"""
    <div class="cosmic-header">
        <h1 class="cosmic-title">‚ú® {texts[lang]['welcome']} ‚ú®</h1>
        <p class="cosmic-subtitle">üåç {texts[lang]['subtitle']} üåç</p>
    </div>
    """
    put_html(header)
    
    for place in places:
        card = f"""
        <div class="place-card-3d">
            <div class="card-inner">
                <div class="card-face">
                    <img src="{place['image']}" alt="{place['name'][lang]}">
                    <div class="card-overlay">
                        <div class="card-icon">{place['icon']}</div>
                        <h3 class="card-title">{place['name'][lang]}</h3>
                        <p class="card-desc">{place['desc'][lang]}</p>
                    </div>
                </div>
            </div>
        </div>
        """
        put_html(card)
        
        put_html(f'<div style="text-align: center; margin: -20px 0 50px 0;">')
        put_buttons([{
            'label': f"‚ú® {texts[lang]['book_now']}",
            'value': place['name'][lang],
            'color': 'primary'
        }], onclick=lambda val=place['name'][lang]: show_booking(val, lang))
        put_html('</div>')

