def show_booking(place_name, lang):
    clear()
    inject_futuristic_css()
    
    put_html(f"""
    <div class="cosmic-header">
        <h2 class="cosmic-title">ğŸ« {texts[lang]['book_now']}</h2>
        <p class="cosmic-subtitle">ğŸ“ {place_name}</p>
    </div>
    """)
    
    data = input_group("", [
        input(f"ğŸ‘¤ {texts[lang]['name']}", name="name", required=True),
        input(f"ğŸ“ {texts[lang]['phone']}", name="phone", required=True, 
              placeholder="+972-XX-XXX-XXXX"),
        input(f"ğŸ“§ {texts[lang]['email']}", name="email", required=True, 
              placeholder="example@email.com"),
        input(f"ğŸ“… {texts[lang]['date']}", name="date", type="date", required=True),
        input(f"ğŸ‘¥ {texts[lang]['people']}", name="people", type="number", 
              required=True, value=1, min=1, max=50),
        input(f"ğŸ’¬ {texts[lang]['notes']}", name="notes", type="text", 
              placeholder="Ø§ÙƒØªØ¨ Ø£ÙŠ Ù…Ù„Ø§Ø­Ø¸Ø§Øª... / Write notes... / ×›×ª×•×‘ ×”×¢×¨×•×ª...")
    ])
    
    if not data['name'] or not validate_phone(data['phone']) or not validate_email(data['email']) or not validate_date(data['date']):
        put_error(f"âŒ {texts[lang]['validation']}")
    else:
        success = send_email(place_name, data['name'], data['phone'], data['email'], 
                           data['date'], data['people'], data['notes'])
        if success:
            put_success(f"{texts[lang]['success']}")
        else:
            put_error(f"{texts[lang]['error']}")
    
    put_html("<br><br>")
    put_buttons([{
        'label': f"ğŸ  {texts[lang]['back']}",
        'value': 'back'
    }], onclick=lambda _: show_places(lang))

